import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";

export default function Dashboard() {
    const [userData, setUserData] = useState(null);
    const [error, setError] = useState("");

    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState("");

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                const token = localStorage.getItem("token");
                if (!token) {
                    setError("トークンがありません。ログインしてください。");
                    return;
                }

                const response = await axios.get("http://localhost:8000/api/dashboard", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setUserData(response.data.user);
            } catch (err) {
                setError("データ取得に失敗しました。再ログインしてください。");
            }
        };

        fetchDashboardData();
    }, []);

    const addTask = () => {
        if (newTask.trim() === "") return;
        setTasks([...tasks, { id: Date.now(), text: newTask }]);
        setNewTask("");
    };

    const deleteTask = (id) => {
        setTasks(tasks.filter((task) => task.id !== id));
    };

    if (error) return <p style={{ color: "red" }}>{error}</p>;
    if (!userData) return <p>読み込み中...</p>;

    return (
        <div className="dashboard-container">
            <h2 className="dashboard-title">ダッシュボードへようこそ！</h2>
            <p className="dashboard-info">ユーザー名: {userData.name}</p>
            <p className="dashboard-info">メールアドレス: {userData.email}</p>
    
            <hr />
    
            <h3 className="dashboard-title">TODOリスト</h3>
            <input
                type="text"
                placeholder="新しいタスクを入力"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
            />
            <button onClick={addTask}>追加</button>
    
            <ul>
                {tasks.map((task) => (
                    <li key={task.id}>
                        {task.text} <button onClick={() => deleteTask(task.id)}>削除</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
